mapper.select:
SELECT FNO, WRITER, CONTENT, IP, HIT, CREATED, LASTMODIFIED, STATE, DEPTH, GROUPNO, GROUPORD
  FROM FREE
 WHERE FNO = #{fNo}

mapper.update:
UPDATE FREE
   SET HIT = HIT +1
 WHERE FNO =#{fNo}

UPDATE FREE
   SET CONTENT = #{content}, LASTMODIFIED = SYSDATE
 WHERE FNO = #{fNo}

UPDATE FREE
   SET STATE = -1
 WHERE FNO = #{fNo}

UPDATE FREE
   SET GROUPORD = GROUPORD + 1
 WHERE GROUPNO = #{groupNo}    
   AND GROUPORD > #{groupOrd}
